INSERT INTO T_PROGRAM VALUES ('FrlptGN8unhuJHJDhiPl' , '가디언즈 오브 갤럭시 VOL.2' , 'programBasic' , '최강 빌런 ‘타노스’에 맞서 은하계를 구하고 최고의 해결사로 등극한 ‘가.오.갤’ 멤버들. 하지만 외계 여사제 ‘아이샤’가 맡긴 임무를 수행하던 중 실수로 또 다시 쫓기는 신세로 전락한다. 한편 자신에게 숨겨진 힘의 원천에 대해 고민하던 리더 ‘스타로드’는 갑작스레 나타난 아버지로 인해 또 다른 위기에 빠지게 되는데…' , 0 , 'Y', now() , now()  );
